index.css.src:
/* Variables */

:root {
  --column-wide: 1200px;
  --column-mid: 940px;
  --column-small: 860px;
  --column-narrow: 600px;
  --font-sans: "Colfax", Helvetica, sans-serif;
  --font-serif: "FreightTextPro", "Hoefler Text", Palatino, serif;
  --primary-color: #009e8c; /* 0, 158, 140 */
  --primary-color-lighter: #ccefeb;
  --primary-color-darker: color(var(--primary-color) shade(15%));
  --text-color: #222;
  --max-width: 100%;
}

/* Imports */

@import "./utilities/reset";
@import "./utilities/accessibility";
@import "./utilities/clearfix";
@import "./fonts/colfax";
@import "./fonts/freight-text-pro";
@import "./fonts/font-awesome";

/* Base */

* {
  box-sizing: border-box;
}

html {
  font-size: 62.5%;
}

body {
  background-color: #fff;
  color: var(--text-color);
  font-family: var(--font-serif);
  font-size: 17px;
  font-size: 1.7rem;
  line-height: 1.3;
  font-smoothing: antialiased;
  -webkit-font-smoothing: antialiased;
}

@import "./copy";

/* Basic layout */

.column-wrapper {
  padding-left: 35px;
  padding-right: 35px;
}

.column-wide,
.column-mid,
.column-small,
.column-narrow {
  margin-left: auto;
  margin-right: auto;
}
.column-wide {
  max-width: var(--column-wide);
}
.column-mid {
  max-width: var(--column-mid);
}
.column-small {
  min-width: var(--column-small);
}
.column-narrow {
  max-width: var(--column-narrow);
}

.block-link {
  color: var(--primary-color);
  display: block;
  font-family: var(--font-sans);
  font-weight: bold;
  text-align: center;
  padding-top: 35px;
  padding-bottom: 35px;
  margin-bottom: 35px;
}
  .block-link:hover {
    color: #222;
    text-decoration: underline;
  }

.section--normal {
  margin-left: auto;
  margin-right: auto;
}

.intro {
  text-align: center;
}
  .intro--small {
    font-size: 17px;
    font-size: 1.7rem;
  }

  .section-header {
    text-align: center;
    padding-top: 15px;
    padding-bottom: 35px;
  }

  .section-header h2 {
    color: #777;
    font-family: var(--font-sans);
    -webkit-font-smoothing: subpixel-antialiased;
    font-smoothing: subpixel-antialiased;
    font-size: 14px;
    font-size: 1.4rem;
    letter-spacing: 0.15em;
    text-transform: uppercase;
    padding-bottom: 12px;
  }

.page__intro {
  padding-bottom: 35px;
}
  .page__intro.copy {
    font-size: 25px;
    font-size: 2.5rem;
  }

/* Components */

@import "./masthead";
@import "./masthead-contextual";
@import "./footer";
@import "./pagination";
@import "./figure";
@import "./sub-page-nav";
@import "./video-player";
@import "./subscribe";
@import "./topics-section";
@import "./blog-post";
@import "./topic";

utilities/reset.css.src:
/* 1. Keeps page centred in all browsers regardless of content height
 * 2. Prevents iOS text size adjust after orientation change, without disabling user zoom
 *    www.456bereastreet.com/archive/201012/controlling_text_size_in_safari_for_ios_without_disabling_user_zoom/
 */
html {
overflow-y: scroll; /* 1 */
-webkit-text-size-adjust: 100%; /* 2 */
-ms-text-size-adjust: 100%; /* 2 */
text-size-adjust: 100%; /* 2 */
}


/* 1. Clear default browser margin
 * 2. Default line-height inconsistent across browsers
 */
body {
margin:0; /* 1 */
line-height:1; /* 2 */
}

/* Forms
 * ------------------------------------------------
 */

form {
margin:0;
}

fieldset {
margin:0;
padding:0;
border:0;
}


/* 1. Corrects font size not being inherited in all browsers
 * 2. Addresses margins set differently in IE6/7, F3/4, S5, Chrome
 * 3. Improves appearance and consistency in all browsers
 */
button,
input,
select,
textarea {
font-size: 100%; /* 1 */
font-family:inherit; /* 1 */
margin: 0; /* 2 */
padding: 0; /* 2 */
vertical-align: baseline; /* 3 */
*vertical-align: middle; /* 3 */
}

/* 1. Addresses FF3/4 setting line-height using !important in the UA stylesheet
 * 2. Corrects inner spacing displayed oddly in IE6/7
 */
button,
input {
line-height: normal; /* 1 */
overflow: visible; /* 2 */
}


/* 1. Removes default vertical scrollbar in IE6/7/8/9
 * 2. Improves readability and alignment in all browsers
 */
textarea {
overflow: auto;
vertical-align: top;
padding: 0;
}


/* 1. Addresses appearance set to searchfield in S5, Chrome
 * 2. Addresses box sizing set to border-box in S5, Chrome (include -moz to future-proof)
 */
input[type="search"] {
  -webkit-appearance: textfield; /* 1 */
  -moz-box-sizing: content-box;
  -webkit-box-sizing: content-box; /* 2 */
  box-sizing: content-box;
}


/* Corrects inner padding displayed oddly in S5, Chrome on OSX */
input[type="search"]::-webkit-search-decoration {
  -webkit-appearance: none;
}

/* Remove the webkit glow */
:focus {
outline: none;
}


/* 1. Addresses box sizing set to content-box in IE8/9
 * 2. Addresses excess padding in IE8/9
*/
input[type="checkbox"],
input[type="radio"] {
  box-sizing: border-box; /* 1 */
  padding: 0; /* 2 */
}


/* Corrects inner padding and border displayed oddly in FF3/4
 * www.sitepen.com/blog/2008/05/14/the-devils-in-the-details-fixing-dojos-toolbar-buttons/
 */
button::-moz-focus-inner,
input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

/* Images
 * ------------------------------------------------
 *
 * Chrome gives figures margin
 */
figure {
margin:0;
}

/* 1. Nicer image sizing in IE
 * 2. Common default
 */
img {
-ms-interpolation-mode: bicubic; /* 1 */
display:block; /* 2 */
}

/* Lists
 * ------------------------------------------------
 */

ol,
ul {
margin:0;
padding:0;
}

dl,
dd {
margin:0;
}

/* Reset list items back to normal block display.
 * This allows us to use lists for semantic markup
 * without needing to set this each time.
 * Lists only look like lists in blocks of copy.
 */
li {
display: block;
padding: 0;
margin: 0;
list-style: none;
}

/* Headings
 * ------------------------------------------------
 */


/* Resets all headings to look like normal text */
h1,
h2,
h3,
h4,
h5,
h6 {
font-weight:inherit;
line-height:inherit;
font-size:inherit;
margin:0;
}

/* Block Text
 * ------------------------------------------------
 */

p {
margin:0;
}

blockquote {
margin:0;
}

/* Improve the appearance of preformatted text */
pre {
  white-space: pre;
  white-space: pre-wrap;
  word-wrap: break-word;
margin:0;
font-family:inherit;
font-size:inherit;
}

/* Inline Text
 * ------------------------------------------------
 */

cite {
font-style:normal;
}

ins {
text-decoration:none;
}

dfn {
font-style:inherit;
}

del {
text-decoration:none;
}

mark {
background:none;
color:inherit;
}

address {
font-style:normal;
}

tt, code, kbd, samp {
font-family: inherit;
font-size:inherit;
}

b,
strong {
font-weight:inherit;
}

em {
font-style:inherit;
}

small {
font-size:100%;
}

q {
  quotes: none;
}

q:before,
q:after {
  content: '';
  content: none;
}

a {
font-weight:inherit;
color:inherit;
text-decoration:none;
}

a:hover,
a:active {
  outline: none;
}

a img{
border: none;
}

/* Set sub, sup without affecting line-height: gist.github.com/413930 */
sub,sup {
font-size: 75%;
line-height: 0;
position: relative;
}

sup {
top: -0.5em;
}

sub {
bottom: -0.25em;
}

/* HTML 5 Block Elements
 * ------------------------------------------------
 */

article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
menu,
nav,
section {
display:block;
}

/* Tables
 * ------------------------------------------------
 */

table {
border-collapse: collapse;
border-spacing: 0;
}

th {
font-weight:inherit;
}

utilities/clearfix.css.src:
/* Clearfixing */

.clearfix:before,
.clearfix:after {
  content: " ";
  display: table;
}

.clearfix:after {
  clear: both;
}

.clearfix {
  *zoom: 1;
}

masthead/index.css.src:
/* Masthead */

.masthead {
  background-color: var(--primary-color);
  font-family: var(--font-sans);
}
  .masthead__inner {
    padding-top: 15px;
    padding-left: 35px;
    padding-right: 35px;
  }

  .masthead__navigation {
    padding-bottom: 25px;
  }

    .masthead-nav-main,
    .masthead-nav-secondary {
      list-style: none;
    }

    .masthead-nav-main {
      float: left;
      margin-left: -15px;
    }

      .masthead-nav-main__item,
      .masthead-nav-secondary__item {
        float: left;
      }

      .masthead-nav-main__item {
        position: relative;
      }

      .masthead-nav-main__item--has-dropdown--hover,
      .masthead-nav-main__item--has-dropdown:focus {
        background-color: var(--primary-color-darker);
      }

      .masthead-nav-main__item--has-dropdown--hover .masthead-nav-main__anchor ,
      .masthead-nav-main__item--has-dropdown:focus .masthead-nav-main__anchor {
        color: #fff;
      }

      .masthead-nav-main__item--has-dropdown--hover .masthead-nav-main__dropdown,
      .masthead-nav-main__item--has-dropdown:focus .masthead-nav-main__dropdown  {
        display: block;
      }

        .masthead-nav-main__anchor {
          color: var(--primary-color-lighter);
          display: inline-block;
          font-size: 17px;
          font-size: 1.7rem;
          padding: 18px;
        }

        .masthead-nav-main__anchor:hover,
        .masthead-nav-main__anchor:focus {
          color: #fff;
        }

    .masthead-nav-main__dropdown {
      background-color: var(--primary-color-darker);
      display: none;
      min-width: 460px;
      padding: 30px 18px 20px;
      position: absolute;
    }

    .masthead-nav-main__dropdown-icon {
      font-size: 8px;
      padding-left: 8px;
      padding-bottom: 3px;
      vertical-align: middle;
    }

      .masthead-nav-main__dropdown-list {
        float: left;
        list-style: none;
        padding-right: 60px;
      }

      .masthead-nav-main__dropdown-list:last-child {
        padding-right: 0;
      }

        .masthead-nav-main__dropdown-item {
          color: var(--primary-color-lighter);
          font-size: 17px;
          font-size: 1.7rem;
          padding-bottom: 12px;
        }

        .masthead-nav-main__dropdown-item:last-child {
          padding-bottom: 0;
        }

          .masthead-nav-main__dropdown-item span {
            color: #fff;
            display: block;
            font-size: 11px;
            font-size: 1.1rem;
            letter-spacing: .2em;
            padding-bottom: 12px;
            text-transform: uppercase;
          }

          .masthead-nav-main__dropdown-anchor:hover,
          .masthead-nav-main__dropdown-anchor:focus {
            color: #fff;
          }

    .masthead-nav-secondary {
      padding-top: 18px;
      float: right;
    }

      .masthead-nav-secondary__item {
        padding-right: 30px;
      }

      .masthead-nav-secondary__item:last-child {
        padding-right: 0;
      }

      .masthead-nav-secondary__item--search {
        color: var(--primary-color-lighter);
        font-size: 11px;
      }

        .masthead-nav-secondary__item--search .masthead-nav-secondary__anchor {
          display: block;
          padding: 6px;
          margin-top: -3px;
        }

        .masthead-nav-secondary__item--search .masthead-nav-secondary__anchor:hover,
        .masthead-nav-secondary__item--search .masthead-nav-secondary__anchor:focus {
          background-color: var(--primary-color-darker);
        }

        .masthead-nav-secondary__anchor {
          color: var(--primary-color-lighter);
          font-size: 11px;
          font-size: 1.1rem;
          font-weight: bold;
          letter-spacing: .2em;
          text-transform: uppercase;
        }

        .masthead-nav-secondary__anchor:hover,
        .masthead-nav-secondary__anchor:focus {
          color: #fff;
        }

  .masthead__logo {
    height: 50px;
    margin-left: 80px;
    width: 244px;
  }

    .masthead__logo-anchor {
      background-image: url("wheeler-centre-logo.svg");
      background-size: 244px 50px;
      color: transparent;
      display: block;
      font: 0/0 a;
      height: 100%;
      width: 100%;
    }

