    padding-left: 35px;
    padding-top: 45px;
    padding-bottom: 25px;
    text-transform: uppercase;
  }

  .contextual__navigation-list {
    float: left;
    padding-left: 35px;
    padding-top: 25px;
  }

  .contextual__navigation-item {
    float: left;
    padding-right: 40px;
  }

  .contextual__navigation-anchor {
    border-bottom: 2px solid #fff;
    color: var(--primary-color);
    display: inline-block;
    padding-bottom: 10px;
  }

  .contextual__navigation-anchor:hover,
  .contextual__navigation-anchor:focus {
    color: #4a4a4a;
  }

  .contextual__navigation-anchor--active {
    border-color: #d9d9d9;
    color: #4a4a4a;
  }

  .upcoming-events {
    float: right;
    padding-right: 35px;
    margin-top: -80px;
  }

    .upcoming-events__navigation {
      height: 30px;
    }

      .upcoming-events__up-next {
        color: var(--primary-color-lighter);
        display: inline-block;
        font-size: 11px;
        font-size: 1.1rem;
        float: left;
        letter-spacing: 0.2em;
        padding-left: 2px;
        padding-right: 30px;
        text-transform: uppercase;
      }

      .upcoming-events__view-all {
        color: var(--primary-color-lighter);
        float: right;
        font-size: 16px;
        font-size: 1.6rem;
      }

      .upcoming-events__view-all:hover,
      .upcoming-events__view-all:focus {
        color: #fff;
      }

      .upcoming-events__view-all i {
        font-size: 8px;
        padding-left: 12px;
        vertical-align: middle;
      }

    .upcoming-events__item {
      border-left: 2px solid #66dacc;
      float: left;
      width: 187px;
    }

    .upcoming-events__item-anchor {
      display: block;
    }

    .upcoming-events__item-anchor--hover .upcoming-events__item-image,
    .upcoming-events__item-anchor:focus .upcoming-events__item-image {
      transform: translateY(-50px);
      transition-property: transform;
      transition-duration: 100ms;
    }

    .upcoming-events__item-anchor--hover .upcoming-events__item-text-container,
    .upcoming-events__item-anchor:focus .upcoming-events__item-text-container {
      background-color: #f1fffd;
    }

      .upcoming-events__item-image-container {
        display: block;
        height: 50px;
        width: 185px;
        position: relative;
      }

        .upcoming-events__item-image {
          display: block;
        }

      .upcoming-events__item-text-container {
        background: #fff;
        display: block;
        padding: 15px 10px 6px;
        position: relative;
        z-index: 2;
      }

        .upcoming-events__item-date {
          color: #6e6e6e;
          font-size: 13px;
          font-size: 1.3rem;
          padding-bottom: 5px;
        }

        .upcoming-events__item-title {
          color: #4a4a4a;
          font-size: 16px;
          font-size: 1.6rem;
          overflow: hidden;
          text-overflow: ellipsis;
          white-space: nowrap;
          width: 100%;
        }

