/* Contextual */

.footer {
  background-color: var(--primary-color);
  color: #fff;
  padding: 25px 35px 35px;
}

.footer-nav-main {
  float: left;
  max-width: 900px;
  width: 60%;
}
  .footer-nav-main__group {
    float: left;
    font-family: var(--font-sans);
    padding-bottom: 20px;
    width: 20%;
  }
    .footer-nav-main__group h3 {
      font-weight: bold;
      font-size: 17px;
      font-size: 1.7rem;
      margin-bottom: 6px;
    }
    .footer-nav-main__group ul {
