blog-post/index.css.src:
.blog-post {
  padding-top: 70px;
  padding-bottom: 50px;
}

.blog-post__header {
  font-family: var(--font-sans);
  text-align: center;
  margin-bottom: 50px;
}

  .blog-post__byline {
    color: #888;
    font-size: 15px;
    font-size: 1.5rem;
    margin-bottom: 15px;
  }
    .blog-post__byline a {
      text-decoration: underline;
      color: #666;
    }
      .blog-post__byline a:hover,
      .blog-post__byline a:focus {
        color: #222;
      }

    .blog-post__byline-separator {
      padding-left: 12px;
      padding-right: 12px;
    }

  .blog-post__title {
    color: var(--primary-color);
    font-size: 48px;
    font-size: 4.8rem;
    font-weight: bold;
  }

.blog-post__intro {
  padding-top: 50px;
  padding-bottom: 15px;
}
  .blog-post__intro:after {
    content: "";
    border-bottom: 1px solid #dfdfdf;
    display: block;
    margin-left: auto;
    margin-right: auto;
    margin-top: 40px;
    margin-bottom: 25px;
    width: 30%;
  }

  .blog-post__body .section--full {
    padding-bottom: 30px;
    padding-top: 30px;
    margin-left: -35px;
    margin-right: -35px;
  }

  .blog-post__intro .section--normal,
  .blog-post__body .section--normal {
    max-width: var(--column-mid);
    position: relative;
  }
  .breakpoint-widescreen-wide .blog-post__body .section--normal > *:not(div),
  .breakpoint-widescreen .blog-post__body .section--normal > *:not(div),
  .breakpoint-tablet-wide .blog-post__body .section--normal > *:not(div) {
    /* Shift _most_ of the content over */
    margin-right: 25%;
    margin-right: calc(25% + 3rem);
  }
  /* Hide the intro line when there's a full-width section as the first thing in the body */
  .blog-post__intro + .blog-post__body .section--full:first-child .figure:first-child {
    margin-top: -71px;
  }


