// playerjs is available as a global variable `playerjs`
require("../vendor/playerjs");
var reactive = require("component/reactive");
var hms = require("yields/hms");
var TouchPreview = require("../touch-preview");

function AudioPlayer(el, props) {
  this.el = el;

  // Set up the adapter
  this.adapter = new playerjs.HTML5Adapter(this.el.querySelector("audio"));
  this.adapter.ready();
  console.log(this.adapter);

  // Bind receiver events
  this.adapter.receiver.on("ready", this.onPlayerReady.bind(this));
  this.adapter.receiver.on("play", this.onPlayerReady.bind(this));
  // this.adapter.receiver.getDuration(this.onPlayerGetDuration.bind(this));
}


AudioPlayer.prototype.onPlayerReady = function() {
  console.log("ready");
};


AudioPlayer.prototype.onPlayerGetDuration = function(seconds) {
  console.log(seconds);
};


// Export the creation function
module.exports = AudioPlayer;

