    border-top: 1px solid #e9e9e9;
  }

  .pagination-top .pagination {
    background: -webkit-gradient(linear, 0% 50%, 100% 50%, color-stop(0%, #fff), color-stop(20%, rgba(250, 250, 250, 0))), -webkit-gradient(linear, 100% 50%, 0% 50%, color-stop(0%, #fff), color-stop(20%, rgba(250, 250, 250, 0.02))), -webkit-gradient(linear, 50% 100%, 50% 0%, color-stop(0%, transparent), color-stop(100%, rgba(0, 0, 0, 0)));
    background: -webkit-linear-gradient(left, #fff 0%, rgba(250, 250, 250, 0) 20%), -webkit-linear-gradient(right, #fff 0%, rgba(250, 250, 250, 0) 20%), -webkit-linear-gradient(top, transparent, rgba(0, 0, 0, 0.02));
    background: -moz-linear-gradient(left, #fff 0%, rgba(250, 250, 250, 0) 20%), -moz-linear-gradient(right, #fff 0%, rgba(250, 250, 250, 0) 20%), -moz-linear-gradient(top, transparent, rgba(0, 0, 0, 0.02));
    background: -o-linear-gradient(left, #fff 0%, rgba(250, 250, 250, 0) 20%), -o-linear-gradient(right, #fff 0%, rgba(250, 250, 250, 0) 20%), -o-linear-gradient(top, transparent, rgba(0, 0, 0, 0.02));
    background: linear-gradient(left, #fff 0%, rgba(250, 250, 250, 0) 20%), linear-gradient(right, #fff 0%, rgba(250, 250, 250, 0) 20%), linear-gradient(top, transparent, rgba(0, 0, 0, 0.02));
    border-bottom: 1px solid #e9e9e9;
